import React from 'react';
import {
    BrowserRouter as Router,
    Link
} from "react-router-dom";
import './styles.css'

const useSortableData = (items, config = null) => {
    const [sortConfig, setSortConfig] = React.useState(config);

    const sortedItems = React.useMemo(() => {
        let sortableItems = [...items];
        if (sortConfig !== null) {
            sortableItems.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortableItems;
    }, [items, sortConfig]);

    const requestSort = (key) => {
        let direction = 'ascending';
        if (
            sortConfig &&
            sortConfig.key === key &&
            sortConfig.direction === 'ascending'
        ) {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    return { items: sortedItems, requestSort, sortConfig };
};

const OthersQuestion = (props) => {
    const { items, requestSort, sortConfig } = useSortableData(props.products);
    const getClassNamesFor = (name) => {
        if (!sortConfig) {
            return;
        }
        return sortConfig.key === name ? sortConfig.direction : undefined;
    };
    return (
        <Router>
            <div>
                <table>
                    <caption><h3>Want to Help Others?</h3></caption>
                    <thead>
                        <tr>
                            <th>
                                <button
                                    type="button"
                                    onClick={() => requestSort('name')}
                                    className={getClassNamesFor('name')}
                                >
                                    Name
            </button>
                            </th>
                            <th>
                                <button
                                    type="button"
                                    onClick={() => requestSort('price')}
                                    className={getClassNamesFor('price')}
                                >
                                    Price
            </button>
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((item) => (
                            <tr key={item.id}>
                                <td>{item.name}</td>
                                <td>${item.price}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <Link to="/global-questions">Go to list global questions</Link>
            </div>
        </Router>
    );
};

export default function App() {
    return (
        <div className="App">
            <OthersQuestion
                products={[
                    { id: 1, name: 'Cheese', price: 4.9 },
                    { id: 2, name: 'Milk', price: 1.9 },
                    { id: 3, name: 'Yoghurt', price: 2.4 },
                ]}
            />
        </div>
    );
}
