import React, { useEffect, useState, useContext } from 'react'
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import { useParams } from "react-router-dom";
import { publicFetch } from '../../util/fetcher'
import { FetchContext } from '../../store/fetch'

const QuestionDetail = ({ questionId, title }) => {
    const { id } = useParams()
    const [question, setQuestion] = useState(null)
    const { authAxios } = useContext(FetchContext)

    useEffect(() => {
        const fetchQuestion = async () => {
            const { data } = await publicFetch.get(`/question/${id}`)
            setQuestion(data)
        }

        fetchQuestion()
    })

    const onSubmit = async (value) => {
        try {
            const { data } = await authAxios.post(`/answer/${id}`, value)
            setQuestion(data)
        } catch (error) {
            console.log(error)
        }
    }


    return (
        <div>
            {question && (
                <>
                    <h2 style={{ margin: "10px" }}>{question.title}</h2>
                    {question.answers.length > 0 && (
                        question.answers.sort().map((answer) => (
                            <div>
                                <div style={{ border: "1px solid black", margin: "10px", padding: "10px", width: "40%", height: "30%" }}>{answer.text}</div>
                                <div style={{ marginBottom: "50px" }}>
                                    <ThumbUpAltIcon style={{
                                        marginLeft: "10px",
                                        margin: "0px",
                                        padding: "0px",
                                        display: "inline"
                                    }} />
                                    <div style={{
                                        marginLeft: "200px",
                                        display: "inline"
                                    }}>Answer by {answer.author.username} on {answer.created.slice(0, 10)}</div>
                                </div>
                            </div>
                        ))
                    )}
                </>
            )}
        </div>
    )
}

export default QuestionDetail
