import React, { useEffect, useState, useContext } from 'react'
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import { useParams } from "react-router-dom";
import { publicFetch } from '../../util/fetcher'
import { FetchContext } from '../../store/fetch'
import IconButton from '@material-ui/core/IconButton';


const QuestionDetail = ({ questionId, title }) => {
    const { id } = useParams()
    const [question, setQuestion] = useState(null)
    const { authAxios } = useContext(FetchContext)

    useEffect(() => {
        const fetchQuestion = async () => {
            const { data } = await publicFetch.get(`/question/${id}`)
            setQuestion(data)
        }

        fetchQuestion()
    })

    const upVote = async (aid) => {
        const { data } = await authAxios.get(
            `/votes/upvote/${id}/${aid}`
        )
        window.location.reload()
    }

    const g = (id) => {
        console.log(id)
    }

    return (
        <div>
            {question && (
                <>
                    <h2 style={{ margin: "10px" }}>{question.title}</h2>
                    {question.answers.length > 0 && (
                        question.answers.sort().map((answer) => (
                            <div>
                                <div style={{ margin: "10px", padding: "10px", width: "40%", height: "30%" }}>{answer.text}</div>
                                <div style={{ marginBottom: "50px" }}>
                                    <IconButton
                                        id={answer.id}
                                        onClick={() => upVote(answer.id)}
                                        style={{
                                            marginLeft: "20px",
                                            margin: "0px",
                                            padding: "0px",
                                            display: "inline"
                                        }} >
                                        <ThumbUpAltIcon />
                                    </IconButton>
                                    {answer.score}
                                    <div style={{
                                        marginLeft: "200px",
                                        display: "inline"
                                    }}>Answer by {answer.author.username} on {answer.created.slice(0, 10)}</div>
                                </div>
                                <hr />
                            </div>
                        ))
                    )}
                </>
            )}
        </div>
    )
}

export default QuestionDetail
