import React, { useState, useEffect, useContext } from 'react'

import { publicFetch } from '../../../util/fetcher'
import RichTextEditor from 'react-rte';
import { FetchContext } from '../../../store/fetch'
import { AuthContext } from '../../../store/auth'
import { useHistory } from "react-router-dom";

const UserQuestion = (props) => {
  const [questions, setQuestions] = useState(null)
  const [show, setShow] = useState("none");
  const [value, setValue] = useState("")
  const { authAxios } = useContext(FetchContext)
  const { authState } = useContext(AuthContext)
  const [questionId, setQuestionId] = useState("")


  useEffect(() => {
    const fetchQuestion = async () => {
      const { data } = await publicFetch.get('/question')
      setQuestions(data)

    }
    if (questions === null) {
      fetchQuestion()
    }
  })

  const sendAnswer = async (e) => {
    const textValue = {
      "text": value
    }
    console.log(textValue.text)
    try {
      await authAxios.post(`/answer/${e.target.id}`, textValue)
      window.location.reload();
    } catch (error) {
      console.log(error)
    }
  }

  const changeDisplay = (e) => {
    if (show === "none") {
      setShow("inline")
      setQuestionId(e.target.id)
    }
    else setShow("none")
  }

  const onChange = (e) => {
    setValue(e.target.value)
  }

  return (
    <div>
      <table style={{ border: "1px solid black", width: "70%" }}>
        <thead>
          <tr style={{ border: "1px solid black" }}>
            <th style={{ border: "1px solid black" }}>Date</th>
            <th style={{ border: "1px solid black" }}>Question</th>
          </tr>
        </thead>
        <tbody>
          {
            (() => {
              if (props.visible) {
                questions
                  ?.sort()
                  .map(
                    ({
                      id,
                      title,
                      created,
                      author
                    }) => (
                      author.id !== authState.userInfo.id ?
                        <tr key={id} style={{ border: "1px solid black" }}>
                          <td style={{ border: "1px solid black" }}>{created.slice(0, 10)}</td>
                          <td style={{ border: "1px solid black" }}>
                            <div style={{ padding: "15px" }}>
                              <div id={id} onClick={changeDisplay}>{title}</div>
                              {id === questionId ? <div style={{ display: show }}>
                                <br />
                                <input style={{ width: "400px", height: "100px" }} value={value}
                                  onChange={onChange} />
                                <br />
                                <button id={id} onClick={sendAnswer}>Send Answer</button>
                              </div> : null}
                            </div>
                          </td>
                        </tr> : null
                    )
                  )
              }
              else {
                questions
                  ?.sort()
                  .map(
                    (i, {
                      id,
                      title,
                      created,
                      author
                    }) => (
                      author.id !== authState.userInfo.id ?
                        <tr key={id} style={{ border: "1px solid black" }}>
                          <td style={{ border: "1px solid black" }}>{created.slice(0, 10)}</td>
                          <td style={{ border: "1px solid black" }}>
                            <a href={`/question/${id}`}>{title}</a>
                          </td>
                        </tr> : null
                    )
                  )
              }

            })()
          }


          {questions
            ?.sort()
            .map(
              ({
                id,
                title,
                created,
                author
              }) => (
                author.id !== authState.userInfo.id ?
                  <tr key={id} style={{ border: "1px solid black" }}>
                    <td style={{ border: "1px solid black" }}>{created.slice(0, 10)}</td>
                    <td style={{ border: "1px solid black" }}>
                      {props.visible ? <div style={{ padding: "15px" }}>
                        <div id={id} onClick={changeDisplay}>{title}</div>
                        {id === questionId ? <div style={{ display: show }}>
                          <br />
                          <input style={{ width: "400px", height: "100px" }} value={value}
                            onChange={onChange} />
                          <br />
                          <button id={id} onClick={sendAnswer}>Send Answer</button>
                        </div> : null}
                      </div> :
                        <a href={`/question/${id}`}>{title}</a>}
                    </td>
                  </tr> : null
              )
            )}
        </tbody>
      </table>
    </div>
  );
};

export default UserQuestion
